import React, {useState} from 'react';

function SearchBox(props) {

    const [searchValue, setSearchValue] = useState("");

    function searchFilter(value) {
        const searchTerm = searchValue.toLowerCase();
        const fullName = value.toLowerCase();

        return (
            searchTerm &&
            fullName.includes(searchTerm) &&
            fullName !== searchTerm 
        )       
    }

    return (

        <div>
            <input type="text" value={searchValue} placeholder={props.placeholder} onChange={setSearchValue} />
            {props.itemsList.length > 0 && props.itemsList.filter((item) => searchFilter(item.Label)).map((item) => (<div onClick={() => {setSearchValue(item.Label); props.setOption(item)}}>{item.Label}</div>))}
        </div>
    )

}

export default SearchBox;
