import React, {useState} from 'react';
import './Common.css'

function ChooseBox(props) {

    const [searchValue, setSearchValue] = useState("");

    return (
        <div className="ChooseBox">
            <div>
                <select className="SelectOption" value={searchValue} onChange={(e) => {props.setOption(e.target.value); props.setOption(props.itemsList.find((item) => item.Label === e.target.value))}}>
                <option value="" disabled={true} >Selecione o Tipo de Ve√≠culo</option>
                {props.itemsList && props.itemsList
                    .map((item) => (                        
                        <option className="ChooseBoxSelectOption" key={item.Label} onClick={() => { props.setOption(item.Label)}}>
                            { item.Label }
                        </option>
                    ))
                }
                </select>
            </div>
        </div>
    )
}

export default ChooseBox;
