import React, {useState} from 'react';

function ChooseBox(props) {

    return (
        <div>
            <div>
                <select value={this.state.searchValue} onChange={(e) => this.handleChoice(e.target.value)}>
                {this.props.list.length != 0 && this.props.list
                    .map((item) => (                        
                        <option key={item.Label} onClick={() => { this.setValue(item.Label) }}>
                            { item.Label }
                        </option>
                    ))
                }
                </select>
            </div>
        </div>
    )
}

export default ChooseBox;
