import React, {useState} from 'react';

function SearchBox(props) {

    const [searchValue, setSearchValue] = useState("");

    function searchFilter(value, filterType) {

        console.log("searchValue: ", searchValue)

        const searchTerm = searchValue.toLowerCase();
        const fullName = value.toLowerCase();

        if (filterType === "includes") {
            return (
                searchTerm &&
                fullName.includes(searchTerm) &&
                fullName !== searchTerm 
            )       
        } else if (filterType === "startsWith") {
            return (
                searchTerm &&
                fullName.startsWith(searchTerm) &&
                fullName !== searchTerm 
            ) 
        }

    }

    function displayOptions(itemsList){

        const filterIncludeOptions = itemsList.filter((item) => searchFilter(item.Label, "includes"));

        if (filterIncludeOptions.length > 10) {
            const filterStartsWithOptions = itemsList.filter((item) => searchFilter(item.Label, "startsWith"));
        }
    }

    if (props.itemsList) {
        console.log("ItemsList:", props.itemsList)
    }


    return (

        <div>
            <input type="text" value={searchValue} placeholder={props.placeholder} onChange={(e) => setSearchValue(e.target.value)} />
            {props.itemsList && props.itemsList.filter((item) => searchFilter(item.Label)).map((item) => (<div key={item.Value} onClick={() => {setSearchValue(item.Label); props.setOption(item)}}>{item.Label}</div>))}
            {/* {props.itemsList && props.itemsList.filter((item) => searchFilter(item.Label)).map((item) => (<div key={item.Value} onClick={() => {setSearchValue(item.Label); props.setOption(item)}}>{item.Label}</div>))} */}
        </div>
    )

}

export default SearchBox;
