import { React, useState, useEffect } from 'react';
import { GetPlotData } from '../../../interfaces/BackendCalls';
import {
    Chart as ChartJS,
    CategoryScale,
    LinearScale,
    BarElement,
    Title,
    Tooltip,
    Legend,
  } from 'chart.js';
  import { Bar } from 'react-chartjs-2';

const GraphPlotPanel = (props) => {

    const [plotData, setPlotData] = useState({
        "monthArray": [],
        "valueArray": [] 
    });

    useEffect(() => {

        GetPlotData(props.vehicleInfo, props.plotOptions).then((result) => setPlotData({
            "monthArray": result.monthArray,
            "valueArray": result.valueArray
        }));

    }, [props]);

    const options = {
        responsive: true,
        plugins: {
          legend: {
            position: 'top',
          },
          title: {
            display: true,
            text: 'Chart.js Bar Chart',
          },
        },
    };

    const data = {
        lables: plotData["monthArray"],
        datasets: [
          {
            label: 'Dataset 1',
            data: plotData["valueArray"],
            backgroundColor: 'rgba(255, 99, 132, 0.5)',
          }
        ],
    };    

    console.log("plotData", plotData);

    return (
        <div>
            { plotData && 
                <Bar 
                    options={options}
                    data={data}
                />
            }

        </div>
    );
}

export default GraphPlotPanel;
